import React from 'react';
import './style.css';
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';
import Button from 'react-bootstrap/Button'

AllCourses=[];

class CourseListApp extends React.Component {
  constructor (props){
    super (props);
    this.state={
      AllCourses: AllCourses
    };
    this.deleteCourse = this.deleteCourse.bind(this);
  }

  deleteCourse(index){
    AllCourses.splice(index,1);
    this.setState(AllCourses=this.state.AllCourses);
  }

  addCourse(courseName){
    AllCourses.push(courseName);
    this.setState(AllCourses=this.state.AllCourses);
  }


  render (){
    return (
      <div>
        <AppJumbotron title="Course Checklist" />
        <ItemList AllCourses={this.props.initCourses} deleteCourse={this.deleteCourse}/>
        <br />
        <CourseForm addCourse={this.addCourse}/>
        <br />
        <CourseCount count={AllCourses.length} />
      </div>
    );
  }
}

class CourseForm extends React.Component {
  constructor(props) {
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
    componentDidMount() {
      this.refs.courseName.focus();
    }

  handleSubmit(event) {
    event.preventDefault();
    var courseName = this.refs.courseName.value;
    if(courseName) {
      this.props.addCourse({courseName});
      this.refs.form.reset();
    }
  }
  render() { 
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Add a course: <br></br>
          <input type="text" ref="courseName" placeholder="Course Name" />
        </label>&nbsp;
        <Button variant="primary" type="submit">Submit</Button>
      </form>
    );
  }
}


class Item extends React.Component {
  constructor (props){
    super (props);
    this.state = {
      checked: false
    };

    this.handleClick = this.handleClick.bind(this); 
    this.deleteClick = this.deleteClick.bind(this);   
  }
  handleClick (e){
    this.setState({
      checked: !this.state.checked
    });

  }

  deleteClick(){
    var index = parseInt(this.props.index);
    console.log(index);
    this.props.deleteCourse(index);

  }
  render (){
    let text = this.state.checked ? <strike>{this.props.courseName}</strike> : this.props.courseName;
    return (
        <div className="row">
          <div className="col-md-12">
            <input type="checkbox" onClick={this.handleClick} />&nbsp;{text}&nbsp;
            <IconButton aria-label="delete" onClick={this.deleteClick}>
              <DeleteIcon />
            </IconButton>
            <hr />
          </div>
        </div>
    );
  }
}


class ItemList extends React.Component {

  render (){
    var courses = AllCourses.map((course, index)=>{
        return(
          <Item key={index} courseName={course} deleteCourse={this.props.deleteCourse}/>
        );
    });
    return(
      <ul>{courses}</ul>
    );
  }
}

class CourseCount extends React.Component {
  constructor (props){
    super ();
  }
  render (){
    return (
      <h4>There are {this.props.count} courses on your Course Checklist</h4>
    );
  }
}

class AppJumbotron extends React.Component {
  render (){
    return (
      <div className="jumbotron">
        <h2 style={{textAlign: "center",}}>
          {this.props.title}
        </h2>

      </div>
    );
  }
}


export default CourseListApp;
