import React from 'react';
import './style.css';
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';

let course1 = "CSE 1105 Introduction to CSE";
let course2 = "CSE 1320 Intermediate Programming";
let course3 = "CSE 1325 Object-Oriented Programming";

let AllCourses = [course1, course2, course3];

class CourseListApp extends React.Component {
  constructor (props){
    super (props);
    this.deleteCourse = this.deleteCourse.bind(this);
  }

  deleteCourse(index){
    AllCourses.splice(index,1);
    this.setState(AllCourses=this.state.AllCourses);
  }
  render (){
    return (
      <div>
        <AppJumbotron title="Course Checklist" />
        <ItemList deleteCourse={this.deleteCourse}/>
        <br />
        <br />
        <br />
        <CourseCount count={AllCourses.length} />
      </div>
    );
  }
}

class Item extends React.Component {
  constructor (props){
    super (props);
    this.state = {
      checked: false
    };

    this.handleClick = this.handleClick.bind(this); 
    this.deleteClick = this.deleteClick.bind(this);   
  }
  handleClick (e){
    this.setState({
      checked: !this.state.checked
    });

  }

  deleteClick(e){
    var index = parseInt(this.props.index);
    console.log(index);
    this.props.deleteCourse(index);

  }
  render (){
    let text = this.state.checked ? <strike>{this.props.message}</strike> : this.props.message;
    return (
        <div className="row">
          <div className="col-md-12">
            <input type="checkbox" onClick={this.handleClick} />&nbsp;{text}&nbsp;
            <IconButton aria-label="delete" onClick={this.deleteClick}>
              <DeleteIcon />
            </IconButton>
            <hr />
          </div>
        </div>
    );
  }
}


class ItemList extends React.Component {
  constructor (props){
    super (props);
  }

  // deleteCourse(index){
  //   AllCourses.splice(index,1);
  //   this.setState(AllCourses=this.state.AllCourses);
  // }
  render (){
    let items = AllCourses.map(course=>course);

    return (
        <h5>{items}</h5>
    );
  }
}

class CourseCount extends React.Component {
  constructor (props){
    super ();
  }
  render (){
    return (
      <h4>There are {this.props.count} courses on your Course Checklist</h4>
    );
  }
}

class AppJumbotron extends React.Component {
  render (){
    return (
      <div className="jumbotron">
        <h2 style={{textAlign: "center",}}>
          {this.props.title}
        </h2>

      </div>
    );
  }
}


export default CourseListApp;
